site.headStyle:
h1{
    color: black;
    text-align: center;
    position: relative;
    bottom: 80px;
    padding: -60%;
    width: 80%;
    left: 10%;
    right: 10%;
    
}
body {
    font-family: 'Helvetica', 'Arial', 'sans-serif';
}

div{
    background-color: white;
    width: 100%;
    height: 100px;
}

hr {
    height: 3px;
    background-color: black;
    clear: both;
    display: block;
    border: 0;
    z-index: 1000;
}

/* h1 will always have lines at the sides */
h1 {
    position: relative;
    font-size: 30px;
    overflow: hidden;
    text-align: center;
    padding: -60%;
}
h1:before, h1:after {
    position: absolute;
    top: 51%;
    overflow: hidden;
    width: 50%;
    height: 1px;
    content: '\a0';
    background-color: gray;
    padding: -60%;
}
h1:before {
    margin-left: -50%;
    text-align: right;
    padding: -60%;
}
h2{
    text-align: center;
    color: black;
    position: relative;
}
p{
  position: relative;
}


.sidebar {
    height: 100%;
    width:0;
    position: absolute;
    z-index: 1;
    top: 0;
    left: 0;
    background-color: lightgrey;
    overflow-x: hidden;
    transition: 0.2s;
    padding-top: 60px;
    z-index: 100;
    
  }
  
  .sidebar a {
    padding: 8px 8px 8px 32px;
    text-decoration: none;
    font-size: 25px;
    color: black;
    display: block;
    transition: 0.3s;
    z-index: 100;
  }
  
  .sidebar a:hover {
    color: #f1f1f1;
    z-index: 100;
  }
  
  .sidebar .closebtn {
    position: absolute;
    top: 0;
    right: 25px;
    font-size: 36px;
    margin-left: 50px;
    z-index: 100;
  }
  
  .openbtn {
    font-size: 20px;
    cursor: pointer;
    background-color: blue;
    color: white;
    padding: 15px 15px;
    border: none;
    z-index: 100;
    transition: 0.5s;
  }

    .openbtn:hover {
        background-color: lightblue;
        z-index: 100;
        color: black;
    }
  
  #main {
    transition: margin-left .5s;
    padding: 16px;
    z-index: 100;
    background-color: lightgrey;
    margin-top: 0;

  }
  
  /* On smaller screens, where height is less than 450px, change the style of the sidenav (less padding and a smaller font size) */
  @media screen and (max-height: 450px) {
    .sidebar {padding-top: 15px;}
    .sidebar a {font-size: 18px;}
  }

  * {box-sizing: border-box;}
body {font-family: Verdana, sans-serif;}
.mySlides {display: none;}
img {vertical-align: middle;}

/* Slideshow container */
.slideshow-container {
  max-width: 1000px;
  position: relative;
  margin: auto;
  right: 10%;
}

/* Caption text */
.text {
    color: #f2f2f2;
    font-size: 15px;
    padding: 8px 12px;
    position: absolute;
    bottom: 8px;
    max-width: 100%; 
    height: auto;
}

/* Number text (1/3 etc) */
.numbertext {
  color: #f2f2f2;
  font-size: 12px;
  padding: 8px 12px;
  position: absolute;
  top: 0;
}

/* The dots/bullets/indicators */
.dot {
  height: 15px;
  width: 15px;
  margin: 0 2px;
  background-color: #bbb;
  border-radius: 50%;
  display: inline-block;
  transition: background-color 0.6s ease;
}

.active {
  background-color: #717171;
}

/* Fading animation */
.fade {
  animation-name: fade;
  animation-duration: 1.5s;
}

@keyframes fade {
  from {opacity: .4} 
  to {opacity: 1}
}

/* On smaller screens, decrease text size */
@media only screen and (max-width: 300px) {
  .text {font-size: 11px}
}




/* Style the sidenav links and the dropdown button */
.sidenav a, .dropdown-btn {
  padding: 6px 8px 6px 16px;
  text-decoration: none;
  font-size: 25px;
  color: black;
  display: block;
  border: none;
  background: none;
  width:100%;
  text-align: left;
  cursor: pointer;
  outline: none;
}

/* On mouse-over */
.sidenav a:hover, .dropdown-btn:hover {
  color: #f1f1f1;
}

/* Main content */
.main {
  margin-left: 200px; /* Same as the width of the sidenav */
  font-size: 25px; /* Increased text to enable scrolling */
  padding: 0px 10px;
}

/* Add an active class to the active dropdown button */
.active {
  background-color: blue;
  color: white;
}

/* Dropdown container (hidden by default). Optional: add a lighter background color and some left padding to change the design of the dropdown content */
.dropdown-container {
  display: none;
  background-color: lightgrey;
  padding-left: 15px;
}

/* Optional: Style the caret down icon */
.fa-caret-down {
  float: right;
  padding-right: 8px;
}

@media screen and (max-height: 450px) {
  .sidenav {padding-top: 15px;}
  .sidenav a {font-size: 18px;}
}


.slideCaption{
  color: black;
  text-align: left;
  font-size: 25px;
  width: 125%;
}

.btnSetting {
    border: none;
    background-color: blue;
    font-size: 25px;
    text-decoration: none;
    transition: 0.5s;
    padding: 15px 15px;
    z-index: 100;
    position: relative;

